import React, { useEffect } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import {
  Box,
  Grid,
  Card,
  CardContent,
  Typography,
  CircularProgress,
} from '@mui/material';
import { getBranchSales } from '../../store/slices/saleSlice';
import { getBranchStock } from '../../store/slices/stockSlice';

const BranchDashboard = () => {
  const dispatch = useDispatch();
  const { user } = useSelector((state) => state.auth);
  const { sales, loading: salesLoading } = useSelector((state) => state.sale);
  const { stock, loading: stockLoading } = useSelector((state) => state.stock);

  useEffect(() => {
    if (user?.branchId) {
      dispatch(getBranchSales(user.branchId));
      dispatch(getBranchStock(user.branchId));
    }
  }, [dispatch, user]);

  if (salesLoading || stockLoading) {
    return (
      <Box display="flex" justifyContent="center" alignItems="center" minHeight="60vh">
        <CircularProgress />
      </Box>
    );
  }

  const totalSales = sales?.reduce((sum, sale) => sum + sale.total, 0) || 0;
  const averageSaleValue = sales?.length ? totalSales / sales.length : 0;
  const lowStockItems = stock?.filter(item => item.quantity < item.minQuantity) || [];
  const totalStockValue = stock?.reduce((sum, item) => sum + (item.quantity * item.product.price), 0) || 0;

  return (
    <Box p={3}>
      <Typography variant="h4" gutterBottom>
        Branch Dashboard
      </Typography>
      
      <Grid container spacing={3}>
        <Grid item xs={12} md={3}>
          <Card>
            <CardContent>
              <Typography color="textSecondary" gutterBottom>
                Total Sales
              </Typography>
              <Typography variant="h4">
                ${totalSales.toFixed(2)}
              </Typography>
            </CardContent>
          </Card>
        </Grid>

        <Grid item xs={12} md={3}>
          <Card>
            <CardContent>
              <Typography color="textSecondary" gutterBottom>
                Average Sale Value
              </Typography>
              <Typography variant="h4">
                ${averageSaleValue.toFixed(2)}
              </Typography>
            </CardContent>
          </Card>
        </Grid>

        <Grid item xs={12} md={3}>
          <Card>
            <CardContent>
              <Typography color="textSecondary" gutterBottom>
                Low Stock Items
              </Typography>
              <Typography variant="h4" color="error">
                {lowStockItems.length}
              </Typography>
            </CardContent>
          </Card>
        </Grid>

        <Grid item xs={12} md={3}>
          <Card>
            <CardContent>
              <Typography color="textSecondary" gutterBottom>
                Total Stock Value
              </Typography>
              <Typography variant="h4">
                ${totalStockValue.toFixed(2)}
              </Typography>
            </CardContent>
          </Card>
        </Grid>
      </Grid>
    </Box>
  );
};

export default BranchDashboard;
