import React, { useState, useEffect } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import {
  Box,
  Button,
  Dialog,
  DialogTitle,
  DialogContent,
  DialogActions,
  TextField,
  IconButton,
  Typography,
  Alert,
  Card,
  CardContent,
  Grid,
  Autocomplete,
  Table,
  TableBody,
  TableCell,
  TableContainer,
  TableHead,
  TableRow,
  Paper,
  MenuItem,
  FormControl,
  InputLabel,
  Select,
  Tooltip,
  Chip,
  Stack,
} from '@mui/material';
import { DataGrid } from '@mui/x-data-grid';
import {
  Add as AddIcon,
  Delete as DeleteIcon,
  Visibility as VisibilityIcon,
  FileDownload as FileDownloadIcon,
  FilterList as FilterListIcon,
} from '@mui/icons-material';
import { useFormik } from 'formik';
import * as Yup from 'yup';
import { createSale, fetchSales } from '../../store/slices/saleSlice';
import { fetchCustomers } from '../../store/slices/customerSlice';
import { fetchProducts } from '../../store/slices/productSlice';
import { DatePicker } from '@mui/x-date-pickers/DatePicker';
import { LocalizationProvider } from '@mui/x-date-pickers/LocalizationProvider';
import { AdapterDateFns } from '@mui/x-date-pickers/AdapterDateFns';
import * as XLSX from 'xlsx';

const validationSchema = Yup.object({
  customerId: Yup.string().required('Customer is required'),
  items: Yup.array()
    .of(
      Yup.object({
        productId: Yup.string().required('Product is required'),
        quantity: Yup.number()
          .positive('Quantity must be positive')
          .required('Quantity is required'),
      })
    )
    .min(1, 'At least one item is required'),
});

const Sales = () => {
  const dispatch = useDispatch();
  const { sales, loading, error } = useSelector((state) => state.sale);
  const { customers } = useSelector((state) => state.customer);
  const { products } = useSelector((state) => state.product);
  const [open, setOpen] = useState(false);
  const [selectedCustomer, setSelectedCustomer] = useState(null);
  const [cartItems, setCartItems] = useState([]);
  const [viewSale, setViewSale] = useState(null);
  const [filterOpen, setFilterOpen] = useState(false);
  const [filters, setFilters] = useState({
    startDate: null,
    endDate: null,
    customer: null,
    minAmount: '',
    maxAmount: '',
  });

  useEffect(() => {
    dispatch(fetchSales());
    dispatch(fetchCustomers());
    dispatch(fetchProducts());
  }, [dispatch]);

  const formik = useFormik({
    initialValues: {
      customerId: '',
      items: [],
    },
    validationSchema,
    onSubmit: async (values) => {
      try {
        await dispatch(createSale(values)).unwrap();
        handleClose();
      } catch (err) {
        // Error is handled by the sale slice
      }
    },
  });

  const handleOpen = () => {
    setOpen(true);
    formik.resetForm();
    setCartItems([]);
  };

  const handleClose = () => {
    setOpen(false);
    setSelectedCustomer(null);
    setCartItems([]);
    formik.resetForm();
  };

  const handleAddItem = (product) => {
    const existingItem = cartItems.find((item) => item.productId === product.id);
    if (existingItem) {
      setCartItems(
        cartItems.map((item) =>
          item.productId === product.id
            ? { ...item, quantity: item.quantity + 1 }
            : item
        )
      );
    } else {
      setCartItems([
        ...cartItems,
        { productId: product.id, quantity: 1, product },
      ]);
    }
  };

  const handleRemoveItem = (productId) => {
    setCartItems(cartItems.filter((item) => item.productId !== productId));
  };

  const handleUpdateQuantity = (productId, quantity) => {
    if (quantity > 0) {
      setCartItems(
        cartItems.map((item) =>
          item.productId === productId ? { ...item, quantity } : item
        )
      );
    } else {
      handleRemoveItem(productId);
    }
  };

  const calculateTotal = () => {
    return cartItems.reduce(
      (total, item) => total + item.quantity * item.product.price,
      0
    );
  };

  const handleExport = () => {
    const exportData = sales.map(sale => ({
      Date: new Date(sale.date).toLocaleDateString(),
      Customer: sale.customer?.name || '-',
      Total: `$${sale.total.toFixed(2)}`,
      Items: sale.items.length,
      Products: sale.items.map(item => `${item.product.name} (${item.quantity})`).join(', '),
    }));

    const ws = XLSX.utils.json_to_sheet(exportData);
    const wb = XLSX.utils.book_new();
    XLSX.utils.book_append_sheet(wb, ws, 'Sales');
    XLSX.writeFile(wb, 'sales_report.xlsx');
  };

  const handleFilter = () => {
    setFilterOpen(true);
  };

  const applyFilters = () => {
    let filteredSales = [...sales];

    if (filters.startDate) {
      filteredSales = filteredSales.filter(
        sale => new Date(sale.date) >= filters.startDate
      );
    }

    if (filters.endDate) {
      filteredSales = filteredSales.filter(
        sale => new Date(sale.date) <= filters.endDate
      );
    }

    if (filters.customer) {
      filteredSales = filteredSales.filter(
        sale => sale.customer?.id === filters.customer.id
      );
    }

    if (filters.minAmount) {
      filteredSales = filteredSales.filter(
        sale => sale.total >= parseFloat(filters.minAmount)
      );
    }

    if (filters.maxAmount) {
      filteredSales = filteredSales.filter(
        sale => sale.total <= parseFloat(filters.maxAmount)
      );
    }

    return filteredSales;
  };

  const columns = [
    { field: 'date', headerName: 'Date', flex: 1,
      valueFormatter: (params) => new Date(params.value).toLocaleDateString() },
    {
      field: 'customer',
      headerName: 'Customer',
      flex: 1,
      valueGetter: (params) => params.row.customer?.name || '-',
    },
    {
      field: 'total',
      headerName: 'Total',
      flex: 1,
      valueFormatter: (params) => `$${params.value.toFixed(2)}`,
    },
    {
      field: 'items',
      headerName: 'Items',
      flex: 1,
      valueGetter: (params) => params.row.items.length,
    },
    {
      field: 'status',
      headerName: 'Status',
      flex: 1,
      renderCell: (params) => (
        <Chip
          label={params.value}
          color={
            params.value === 'Completed' ? 'success' :
            params.value === 'Pending' ? 'warning' :
            'error'
          }
        />
      ),
    },
    {
      field: 'actions',
      headerName: 'Actions',
      flex: 1,
      renderCell: (params) => (
        <Box>
          <Tooltip title="View Details">
            <IconButton onClick={() => setViewSale(params.row)} color="primary">
              <VisibilityIcon />
            </IconButton>
          </Tooltip>
        </Box>
      ),
    },
  ];

  return (
    <Box>
      <Box sx={{ display: 'flex', justifyContent: 'space-between', mb: 3 }}>
        <Typography variant="h4">Sales</Typography>
        <Stack direction="row" spacing={2}>
          <Button
            variant="outlined"
            startIcon={<FilterListIcon />}
            onClick={handleFilter}
          >
            Filter
          </Button>
          <Button
            variant="outlined"
            startIcon={<FileDownloadIcon />}
            onClick={handleExport}
          >
            Export
          </Button>
          <Button
            variant="contained"
            startIcon={<AddIcon />}
            onClick={handleOpen}
          >
            New Sale
          </Button>
        </Stack>
      </Box>

      {error && (
        <Alert severity="error" sx={{ mb: 2 }}>
          {error}
        </Alert>
      )}

      <Grid container spacing={3} sx={{ mb: 4 }}>
        <Grid item xs={12} md={4}>
          <Card>
            <CardContent>
              <Typography variant="h6" gutterBottom>
                Total Sales
              </Typography>
              <Typography variant="h4">
                ${sales.reduce((total, sale) => total + sale.total, 0).toFixed(2)}
              </Typography>
            </CardContent>
          </Card>
        </Grid>
        <Grid item xs={12} md={4}>
          <Card>
            <CardContent>
              <Typography variant="h6" gutterBottom>
                Number of Sales
              </Typography>
              <Typography variant="h4">{sales.length}</Typography>
            </CardContent>
          </Card>
        </Grid>
        <Grid item xs={12} md={4}>
          <Card>
            <CardContent>
              <Typography variant="h6" gutterBottom>
                Average Sale
              </Typography>
              <Typography variant="h4">
                $
                {(
                  sales.reduce((total, sale) => total + sale.total, 0) /
                  (sales.length || 1)
                ).toFixed(2)}
              </Typography>
            </CardContent>
          </Card>
        </Grid>
        <Grid item xs={12} md={3}>
          <Card>
            <CardContent>
              <Typography variant="h6" gutterBottom>
                Today's Sales
              </Typography>
              <Typography variant="h4">
                $
                {sales
                  .filter(sale => new Date(sale.date).toDateString() === new Date().toDateString())
                  .reduce((total, sale) => total + sale.total, 0)
                  .toFixed(2)}
              </Typography>
            </CardContent>
          </Card>
        </Grid>
      </Grid>

      <Box sx={{ height: 600, width: '100%' }}>
        <DataGrid
          rows={applyFilters()}
          columns={columns}
          pageSize={10}
          rowsPerPageOptions={[10, 25, 50]}
          checkboxSelection
          disableSelectionOnClick
          loading={loading}
        />
      </Box>

      <Dialog open={open} onClose={handleClose} maxWidth="md" fullWidth>
        <DialogTitle>New Sale</DialogTitle>
        <form onSubmit={formik.handleSubmit}>
          <DialogContent>
            <Autocomplete
              options={customers}
              getOptionLabel={(option) => option.name}
              value={selectedCustomer}
              onChange={(event, newValue) => {
                setSelectedCustomer(newValue);
                formik.setFieldValue('customerId', newValue?.id || '');
              }}
              renderInput={(params) => (
                <TextField
                  {...params}
                  label="Customer"
                  error={formik.touched.customerId && Boolean(formik.errors.customerId)}
                  helperText={formik.touched.customerId && formik.errors.customerId}
                />
              )}
            />

            <Box sx={{ mt: 3 }}>
              <Typography variant="h6" gutterBottom>
                Add Products
              </Typography>
              <Autocomplete
                options={products}
                getOptionLabel={(option) => option.name}
                onChange={(event, newValue) => {
                  if (newValue) {
                    handleAddItem(newValue);
                  }
                }}
                renderInput={(params) => (
                  <TextField {...params} label="Select Product" />
                )}
              />
            </Box>

            <TableContainer component={Paper} sx={{ mt: 3 }}>
              <Table>
                <TableHead>
                  <TableRow>
                    <TableCell>Product</TableCell>
                    <TableCell>Price</TableCell>
                    <TableCell>Quantity</TableCell>
                    <TableCell>Total</TableCell>
                    <TableCell>Actions</TableCell>
                  </TableRow>
                </TableHead>
                <TableBody>
                  {cartItems.map((item) => (
                    <TableRow key={item.productId}>
                      <TableCell>{item.product.name}</TableCell>
                      <TableCell>${item.product.price.toFixed(2)}</TableCell>
                      <TableCell>
                        <TextField
                          type="number"
                          value={item.quantity}
                          onChange={(e) =>
                            handleUpdateQuantity(
                              item.productId,
                              parseInt(e.target.value)
                            )
                          }
                          inputProps={{ min: 1 }}
                        />
                      </TableCell>
                      <TableCell>
                        ${(item.quantity * item.product.price).toFixed(2)}
                      </TableCell>
                      <TableCell>
                        <IconButton
                          onClick={() => handleRemoveItem(item.productId)}
                          color="error"
                        >
                          <DeleteIcon />
                        </IconButton>
                      </TableCell>
                    </TableRow>
                  ))}
                </TableBody>
              </Table>
            </TableContainer>

            <Box sx={{ mt: 3, textAlign: 'right' }}>
              <Typography variant="h6">
                Total: ${calculateTotal().toFixed(2)}
              </Typography>
            </Box>
          </DialogContent>
          <DialogActions>
            <Button onClick={handleClose}>Cancel</Button>
            <Button
              type="submit"
              variant="contained"
              disabled={loading || cartItems.length === 0}
            >
              Complete Sale
            </Button>
          </DialogActions>
        </form>
      </Dialog>

      <Dialog open={Boolean(viewSale)} onClose={() => setViewSale(null)} maxWidth="md" fullWidth>
        <DialogTitle>Sale Details</DialogTitle>
        <DialogContent>
          {viewSale && (
            <>
              <Grid container spacing={2} sx={{ mb: 3 }}>
                <Grid item xs={6}>
                  <Typography variant="subtitle1">
                    <strong>Date:</strong> {new Date(viewSale.date).toLocaleString()}
                  </Typography>
                  <Typography variant="subtitle1">
                    <strong>Customer:</strong> {viewSale.customer?.name}
                  </Typography>
                </Grid>
                <Grid item xs={6}>
                  <Typography variant="subtitle1">
                    <strong>Status:</strong>{' '}
                    <Chip
                      label={viewSale.status}
                      color={
                        viewSale.status === 'Completed' ? 'success' :
                        viewSale.status === 'Pending' ? 'warning' :
                        'error'
                      }
                    />
                  </Typography>
                  <Typography variant="subtitle1">
                    <strong>Total:</strong> ${viewSale.total.toFixed(2)}
                  </Typography>
                </Grid>
              </Grid>

              <TableContainer component={Paper}>
                <Table>
                  <TableHead>
                    <TableRow>
                      <TableCell>Product</TableCell>
                      <TableCell>Price</TableCell>
                      <TableCell>Quantity</TableCell>
                      <TableCell>Total</TableCell>
                    </TableRow>
                  </TableHead>
                  <TableBody>
                    {viewSale.items.map((item) => (
                      <TableRow key={item.productId}>
                        <TableCell>{item.product.name}</TableCell>
                        <TableCell>${item.product.price.toFixed(2)}</TableCell>
                        <TableCell>{item.quantity}</TableCell>
                        <TableCell>
                          ${(item.quantity * item.product.price).toFixed(2)}
                        </TableCell>
                      </TableRow>
                    ))}
                  </TableBody>
                </Table>
              </TableContainer>
            </>
          )}
        </DialogContent>
        <DialogActions>
          <Button onClick={() => setViewSale(null)}>Close</Button>
        </DialogActions>
      </Dialog>

      <Dialog open={filterOpen} onClose={() => setFilterOpen(false)} maxWidth="sm" fullWidth>
        <DialogTitle>Filter Sales</DialogTitle>
        <DialogContent>
          <LocalizationProvider dateAdapter={AdapterDateFns}>
            <Grid container spacing={2} sx={{ mt: 1 }}>
              <Grid item xs={12} md={6}>
                <DatePicker
                  label="Start Date"
                  value={filters.startDate}
                  onChange={(newValue) => setFilters({ ...filters, startDate: newValue })}
                  renderInput={(params) => <TextField {...params} fullWidth />}
                />
              </Grid>
              <Grid item xs={12} md={6}>
                <DatePicker
                  label="End Date"
                  value={filters.endDate}
                  onChange={(newValue) => setFilters({ ...filters, endDate: newValue })}
                  renderInput={(params) => <TextField {...params} fullWidth />}
                />
              </Grid>
              <Grid item xs={12}>
                <Autocomplete
                  options={customers}
                  getOptionLabel={(option) => option.name}
                  value={filters.customer}
                  onChange={(event, newValue) => setFilters({ ...filters, customer: newValue })}
                  renderInput={(params) => <TextField {...params} label="Customer" />}
                />
              </Grid>
              <Grid item xs={12} md={6}>
                <TextField
                  fullWidth
                  label="Min Amount"
                  type="number"
                  value={filters.minAmount}
                  onChange={(e) => setFilters({ ...filters, minAmount: e.target.value })}
                />
              </Grid>
              <Grid item xs={12} md={6}>
                <TextField
                  fullWidth
                  label="Max Amount"
                  type="number"
                  value={filters.maxAmount}
                  onChange={(e) => setFilters({ ...filters, maxAmount: e.target.value })}
                />
              </Grid>
            </Grid>
          </LocalizationProvider>
        </DialogContent>
        <DialogActions>
          <Button onClick={() => setFilterOpen(false)}>Cancel</Button>
          <Button
            onClick={() => {
              setFilterOpen(false);
            }}
            variant="contained"
          >
            Apply Filters
          </Button>
        </DialogActions>
      </Dialog>
    </Box>
  );
};

export default Sales;
