import React, { useEffect } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import {
  Box,
  Grid,
  Card,
  CardContent,
  Typography,
  CircularProgress,
} from '@mui/material';
import { getCompanySales } from '../../store/slices/saleSlice';
import { getCompanyProducts } from '../../store/slices/productSlice';

const CompanyDashboard = () => {
  const dispatch = useDispatch();
  const { user } = useSelector((state) => state.auth);
  const { sales, loading: salesLoading } = useSelector((state) => state.sale);
  const { products, loading: productsLoading } = useSelector((state) => state.product);

  useEffect(() => {
    if (user?.companyId) {
      dispatch(getCompanySales(user.companyId));
      dispatch(getCompanyProducts(user.companyId));
    }
  }, [dispatch, user]);

  if (salesLoading || productsLoading) {
    return (
      <Box display="flex" justifyContent="center" alignItems="center" minHeight="60vh">
        <CircularProgress />
      </Box>
    );
  }

  const totalSales = sales?.reduce((sum, sale) => sum + sale.total, 0) || 0;
  const averageSaleValue = sales?.length ? totalSales / sales.length : 0;
  const totalProducts = products?.length || 0;

  return (
    <Box p={3}>
      <Typography variant="h4" gutterBottom>
        Company Dashboard
      </Typography>
      
      <Grid container spacing={3}>
        <Grid item xs={12} md={4}>
          <Card>
            <CardContent>
              <Typography color="textSecondary" gutterBottom>
                Total Products
              </Typography>
              <Typography variant="h4">
                {totalProducts}
              </Typography>
            </CardContent>
          </Card>
        </Grid>

        <Grid item xs={12} md={4}>
          <Card>
            <CardContent>
              <Typography color="textSecondary" gutterBottom>
                Total Sales
              </Typography>
              <Typography variant="h4">
                ${totalSales.toFixed(2)}
              </Typography>
            </CardContent>
          </Card>
        </Grid>

        <Grid item xs={12} md={4}>
          <Card>
            <CardContent>
              <Typography color="textSecondary" gutterBottom>
                Average Sale Value
              </Typography>
              <Typography variant="h4">
                ${averageSaleValue.toFixed(2)}
              </Typography>
            </CardContent>
          </Card>
        </Grid>
      </Grid>
    </Box>
  );
};

export default CompanyDashboard;
